mapper.insert:
insert into vhcl(
	  vhcl_no
	, vhcl_name
	, enabled
	, vhcl_img_path
) values (
	  #{vhclNo}
	, #{vhclName}
	, #{enabled}
	, #{vhclImgPath}
)

mapper.update:
update vhcl
set
	vhcl_no = #{vhclNo}
	,vhcl_name = #{vhclName}
	,enabled = #{enabled}
	,vhcl_img_path = #{vhclImgPath}
where vhcl_no = #{vhclNo}
