mapper.insert:
INSERT INTO auth(
	emp_no
	, auth
)
VALUES (
	#{empNo}
	, #{auth}
)

mapper.update:
UPDATE employee
SET
    emp_name = #{empName}
    , emp_pw = #{empPw}
    , emp_tel = #{empTel}
    , ext_no = #{extNo}
    , emp_email = #{empEmail}
    , emp_psn_email = #{empPsnEmail}
    , emp_birth = #{empBirth}
    , emp_rrn = #{empRrn}
    , emp_zip = #{empZip}
    , emp_addr1 = #{empAddr1}
    , emp_addr2 = #{empAddr2}
    , profile_img_path = #{profileImgPath}
    , sign_img = #{signImg}
    , account_no = #{accountNo}
    , join_day = #{joinDay}
    , dept_code = #{deptCode}
    , position_code = #{positionCode}
    , duty_code = #{dutyCode}
    , bank_code = #{bankCode}
    , leave_day = #{leaveDay}
    , hffc_status = #{hffcStatus}
    , admin_yn = #{adminYn}
WHERE emp_no = #{empNo}
