mapper.select:
select b.* from ( select a.*, row_number() over (order by a.board_no desc) rnum from ( select board_code, board_no, board_title, b.emp_no, board_content, reg_date, inq_cnt, e.emp_name from board b, employee e where 1=1 and b.emp_no = e.emp_no and board_code = #{boardCode}
order by board_no desc ) a ) b LEFT JOIN employee e ON b.emp_no = e.emp_no
where b.rnum >= #{startRow} and b.rnum <= #{endRow}

SELECT COUNT(*) FROM board
where 1=1
and board_code = #{boardCode}

mapper.insert:

    select seq_board.nextval from dual

insert into board(
    board_no,
    emp_no,
    board_code,
    board_title,
    board_content,
    reg_date,
    update_date,
    inq_cnt,
    temp_yn
) values (
    #{boardNo}
    ,#{empNo}
    ,#{boardCode}
    ,#{boardTitle}
    ,#{boardContent}
    ,sysdate
    ,sysdate
    ,0
    ,'N'
)



    select seq_board_attach.nextval from dual


