mapper.insert:
insert into chat_attach(
	  chat_file_no
	, chat_no
	, chat_file_name
	, chat_file_size
	, chat_file_fancysize
	, chat_file_mimetype
	, chat_file_savepath
	, chat_file_downcount
)values (
	  seq_chat_attach.nextval
	, #{chatNo}
	, #{chatFileName}
	, #{chatFileSize}
	, #{chatFileFancysize}
	, #{chatFileMimetype}
	, #{chatFileSavepath}
	, 0
)

mapper.delete:
delete from chat_mem
where chatroom_no = #{chatroomNo}
and emp_no = #{empNo}
