mapper.select:
	select
	b.*
	from(
	select
	a.*, row_number() over (order by a.CMNY_NO desc)
	rnum
	from(
	select
	CMNY_NO, CMNY_NAME, CMNY_TOP, OPEN_DAY, CMNY_IMG_PATH
	from community
	where 1=1

	and CMNY_STATUS = '1'
	order by CMNY_NO desc
	) a
	) b	

  	where b.rnum >= #{startRow} and b.rnum <= #{endRow}

mapper.insert:

	select seq_cmny.nextval from dual



insert into community(
CMNY_NO
,CMNY_NAME
,CMNY_TOP
,CMNY_INTRO
,OPEN_DAY
,CMNY_IMG_PATH
) values (
#{cmnyNo}
,#{cmnyName}
,#{cmnyTop}
,#{cmnyIntro}
,TO_CHAR(SYSDATE, 'YYYY.MM.DD')
,#{cmnyImgPath}
)


	select seq_cmny_mem.nextval from dual


insert into community_mem(
CMNY_MEM_NO
,CMNY_NO
,EMP_NO
,JOIN_DAY
,CMNY_MEM_APPROVAL
,CMNY_TOP_YN
) values (
#{cmnyMemNo}
,#{cmnyNo}
,#{empNo}
,TO_CHAR(SYSDATE, 'YYYY.MM.DD')
,'1'
,'Y'
)


	select seq_cmny_mem.nextval from dual


insert into community_mem(
CMNY_MEM_NO
,CMNY_NO
,EMP_NO
,JOIN_DAY
,CMNY_MEM_APPROVAL
,CMNY_TOP_YN
) values (
#{cmnyMemNo}
,#{cmnyNo}
,#{empNo}
,TO_CHAR(SYSDATE, 'YYYY.MM.DD')
,'1'
,'N'
)
