mapper.select:
SELECT drive_folder_no , drive_folder_name , drive_folder_parent , drive_folder_date , emp_no , drive_status , dept_code , fix_drive_status FROM drive_folder WHERE drive_status = #{driveStatus}
AND (emp_no = #{empNo} OR dept_code = #{group})
AND drive_folder_parent IS NULL AND emp_no = #{empNo} AND dept_code IS NULL
AND drive_folder_parent IS NULL AND dept_code = #{group}
order by drive_folder_no desc

SELECT drive_file_no , emp_no , drive_folder_no , drive_file_name , drive_file_size , drive_file_fancysize , drive_file_mimetype , drive_file_savepath , drive_status , drive_file_date , dept_code , fix_drive_status FROM drive WHERE drive_status = #{driveStatus}
AND (emp_no = #{empNo} OR dept_code = #{group})
AND drive_folder_no IS NULL AND emp_no = #{empNo} AND dept_code IS NULL
AND drive_folder_no IS NULL AND dept_code = #{group}
order by drive_file_no desc

SELECT drive_folder_no , drive_folder_name , drive_folder_parent , drive_folder_date , emp_no , drive_status , dept_code , fix_drive_status FROM drive_folder WHERE drive_folder_parent = #{driveFolderNo} AND drive_status = #{driveStatus}
AND (emp_no = #{empNo} OR dept_code = #{group})
AND emp_no = #{empNo} AND dept_code IS NULL
AND dept_code = #{group}
order by drive_folder_no desc

SELECT drive_file_no ,emp_no ,drive_folder_no ,drive_file_name ,drive_file_size ,drive_file_fancysize ,drive_file_mimetype ,drive_file_savepath ,drive_status ,drive_file_date , dept_code , fix_drive_status FROM drive WHERE drive_folder_no = #{driveFolderNo} AND drive_status = #{driveStatus}
AND (emp_no = #{empNo} OR dept_code = #{group})
AND emp_no = #{empNo} AND dept_code IS NULL
AND dept_code = #{group}
order by drive_file_no desc

mapper.insert:

	select seq_drive_folder.nextval from dual

insert into drive_folder(
	  drive_folder_no
    , drive_folder_name
    , drive_folder_parent
    , drive_folder_date
    , emp_no
    , drive_status
    , dept_code
    , fix_drive_status
) values (
	  #{driveFolderNo}
	, #{driveFolderName}
	, #{driveFolderParent}
	, sysdate
	, #{empNo}
	, #{driveStatus}
	, #{deptCode}
	, #{driveStatus}
)


	select seq_drive.nextval from dual

insert into drive(
	 drive_file_no
    ,emp_no
    ,drive_folder_no
    ,drive_file_name
    ,drive_file_size
    ,drive_file_fancysize
    ,drive_file_mimetype
    ,drive_file_savepath
    ,drive_status
    ,drive_file_date
    , dept_code
    , fix_drive_status
) values (
	  #{driveFileNo}
	, #{empNo}
	, #{driveFolderNo}
	, #{driveFileName}
	, #{driveFileSize}
	, #{driveFileFancysize}
	, #{driveFileMimetype}
	, #{driveFileSavepath}
	, #{driveStatus}
	, sysdate
	, #{deptCode}
	, #{driveStatus}
)
